import React from "react";
import s from './MyPosts.module.css';
import Post from "./Post/Post";
import { Field, reduxForm } from "redux-form";
import { required } from "../../../units/validators/validators";

const MyPosts = (props) => {
  let postsElement = props.posts.map(p => <Post message={p.message} likeCount={p.likeCount} />);

  let newPostElement = React.createRef();

  let onAddPost = (values) => {
    props.addPost(values.newPostText);
  }

  let onPostChange = () => {
    let text = newPostElement.current.value;
    props.updateNewPostText(text);

  }

  return <div className={s.postsBlock}>
    <h3>My post</h3>
    <PostFormRedux onSubmit={ onAddPost } />

    <div className={s.posts}>
      {postsElement}
    </div>

  </div>

}

const addNewPostForm = (props) => {
  return (
  <form action="" onSubmit={props.handleSubmit}>
      <Field placeholder="Enter your message" name="newPostText" component={'textarea'} validate = {[required]} />
      <div>
      <button>Add post</button>
      </div>
  </form>
  );
}

const PostFormRedux = reduxForm({form: 'prfileMessageForm'})(addNewPostForm);

export default MyPosts;
