import React from "react";


class ProfileStatus extends React.Component {
    state = {
        edidMode: false
    }
    activeEditMode = ()=>{
        this.setState(
            {edidMode: true}
        )
    }

    render() {
        return <div>
            {!this.state.edidMode &&
                <div>
                    <span onDoubleClick={this.activeEditMode}>{this.props.status}</span>
                </div>
            }
            {this.state.edidMode &&
                <div>
                    <input value={this.props.status} />
                </div>
            }
        </div>

    }

}
export default ProfileStatus
