const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
    users: [
        { id: 1, photoUrl:'https://kino.mail.ru/person/447125_dmitrij_nagiev/' ,followed:'false', fullName: 'Vadym', status: 'i am boss', location:{city:'Khmelnickii', country:'Ukraine'} },
        { id: 2, followed:'true', fullName: 'Klaudia', status: 'i am boss too', location:{city:'Pila', country:'Poland'} },
        { id: 3, followed:'false', fullName: 'Marek', status: 'i am boss too', location:{city:'Sydney', country:'Australia'} }
    ],
}

const usersReducer = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state, 
                users: state.users.map(u=>{
                    if (u.id === action.userId) {
                        return {...u, followed:true}
                    }
                    return u;
                })
            }
        case UNFOLLOW:
            return {
                ...state, 
                users: state.users.map(u=>{
                    if (u.id === action.userId) {
                        return {...u, followed:false}
                    }
                    return u;
                })
            }
        case SET_USERS: {
            return { ...state, users:[...state.users, ...action.users] }
        }
        default:
            return state;
    }
}

export const followAC = (userId) => ({ type: FOLLOW, userId })
export const unfollowAC = (userId) => ({ type: UNFOLLOW, userId })
export const setUsersAC = (users) => ({ type: SET_USERS, users })


export default usersReducer;
